import React, { useState } from 'react';
import Table from './components/Table';
import jsonData from './data/collegesData.json';
import './App.css';

function App() {
  const [colleges, ] = useState(jsonData);
  const [sortBy, setSortBy] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc');
  const [searchTerm, setSearchTerm] = useState('');

  const handleSort = sortByField => {
    if (sortByField === sortBy) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(sortByField);
      setSortOrder('asc');
    }
  };

  const handleSearch = event => {
    setSearchTerm(event.target.value);
  };

  return (
    <div className="App">

      <p>
        Task:<br/>
        1) Sort by : To sort the data click on the table heading or title <br/>
        2) Search  : To search the college click on the below searchfeild to search the college <br/>
        3) Infinite Scroll : I have Applied the Infinite Scroll feature but it will work in we hav siomuch data
      </p>

      <input
        type="text"
        placeholder="Search by college name"
        value={searchTerm}
        onChange={handleSearch}
      />
      <br/>
      <br/>
      <br/>
      <Table
        colleges={colleges}
        sortBy={sortBy}
        sortOrder={sortOrder}
        searchTerm={searchTerm}
        onSort={handleSort}
      />
    </div>
  );
}

export default App;
