// CollegeTable.js
import React, { useState, useEffect } from 'react';
import CollegeRow from './CollegeRow';
import SearchBar from './SearchBar';
import InfiniteScroll from './InfiniteScroll';
import collegesData from '../data/collegesData.json';
import '../styles/CollegeTable.css';

const CollegeTable = () => {
  const [colleges, setColleges] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState(null);
  const [sortOrder, setSortOrder] = useState(null);

  useEffect(() => {
    loadInitialColleges();
  }, []);

  const loadInitialColleges = () => {
    setIsLoading(true);
    setTimeout(() => {
      setColleges(collegesData.slice(0, 10));
      setIsLoading(false);
    }, 1000);
  };

  const loadMoreColleges = () => {
    setIsLoading(true);
    setTimeout(() => {
      setColleges(prevColleges => [
        ...prevColleges,
        ...collegesData.slice(prevColleges.length, prevColleges.length + 10)
      ]);
      setIsLoading(false);
    }, 1000);
  };

  const handleSort = (column) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder('asc');
    }
  };

  const sortedColleges = colleges.slice().sort((a, b) => {
    if (!sortBy) return 0;
    if (sortOrder === 'asc') {
      return a[sortBy] > b[sortBy] ? 1 : -1;
    } else {
      return a[sortBy] < b[sortBy] ? 1 : -1;
    }
  });

  const filteredColleges = sortedColleges.filter(college =>
    college.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <SearchBar setSearchTerm={setSearchTerm} />
      <table className="college-table">
        <thead>
          <tr>
            <th onClick={() => handleSort('name')}>College Name</th>
            <th onClick={() => handleSort('cdRank')}>CD Rank</th>
            <th onClick={() => handleSort('courseFees')}>Course Fees</th>
            <th onClick={() => handleSort('featured')}>Featured</th>
          </tr>
        </thead>
        <tbody>
          {filteredColleges.map(college => (
            <CollegeRow key={college.id} college={college} />
          ))}
        </tbody>
      </table>
      {isLoading && <p>Loading...</p>}
      {!isLoading && <InfiniteScroll loadMoreData={loadMoreColleges} />}
    </div>
  );
};

export default CollegeTable;
