import React, { useState, useRef, useEffect } from 'react';
import TableRow from './TableRow';

const Table = ({ colleges, sortBy, sortOrder, searchTerm, onSort }) => {
  const [visibleRows, setVisibleRows] = useState(10); // Number of rows initially visible
  const tableBodyRef = useRef(null);

  useEffect(() => {
    // Add event listener for scroll
    const handleScroll = () => {
      const { scrollTop, scrollHeight, clientHeight } = tableBodyRef.current;
      if (scrollTop + clientHeight >= scrollHeight) {
        // Reached the bottom of the table, increment visible rows
        setVisibleRows(prevVisibleRows => prevVisibleRows + 10);
      }
    };

    // Attach event listener to the table body
    tableBodyRef.current.addEventListener('scroll', handleScroll);

    return () => {
      // Cleanup: remove event listener when component unmounts
      tableBodyRef.current.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Filter and sort the colleges based on search term and sorting criteria
  const filteredColleges = colleges.filter(college =>
    college.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const sortedColleges = filteredColleges.sort((a, b) => {
    if (sortBy) {
      const fieldA = a[sortBy];
      const fieldB = b[sortBy];
      let comparison = 0;
      if (fieldA < fieldB) {
        comparison = -1;
      } else if (fieldA > fieldB) {
        comparison = 1;
      }
      return sortOrder === 'asc' ? comparison : -comparison;
    }
    return 0;
  });

  return (
    <div className="table-container">
      <table>
        <thead>
          <tr>
            <th>CD Rank</th>
            <th>College</th>
            <th>Fees</th>
            <th>AveragePackage</th>
            <th>HighestPackage</th>
            <th>Reviews</th>
            <th>Rank</th>
          </tr>
        </thead>
      </table>
      <div className="table-body" ref={tableBodyRef}>
        <table>
          <tbody>
            {sortedColleges.slice(0, visibleRows).map(college => (
              <TableRow key={college.id} college={college} />
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Table;
