// CollegeTable.js
import React, { useState } from 'react';
import CollegeRow from './CollegeRow';
import SearchBar from './SearchBar';
import InfiniteScroll from './InfiniteScroll';
import collegesData from '../data/collegesData.json';
import '../styles/CollegeTable.css';



// Dummy JSON data of colleges
const dummyColleges = [
  { id: 1, name: 'College A', featured: true },
  { id: 2, name: 'College B', featured: false },
  { id: 3, name: 'College C', featured: true },
  // Add more dummy colleges as needed
];

const CollegeTable = () => {
  const [colleges, setColleges] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    // Load initial data
    loadColleges();
  }, []);

  const loadColleges = () => {
    // Simulate loading data from an API
    setIsLoading(true);
    setTimeout(() => {
      setColleges(dummyColleges.slice(0, 10)); // Load 10 initial colleges
      setIsLoading(false);
    }, 1000);
  };

  const loadMoreColleges = () => {
    // Simulate loading more data from an API
    setIsLoading(true);
    setTimeout(() => {
      setColleges(prevColleges => [
        ...prevColleges,
        ...dummyColleges.slice(prevColleges.length, prevColleges.length + 10) // Load next 10 colleges
      ]);
      setIsLoading(false);
    }, 1000);
  };

  return (
    <div className="college-table-container">
      <table className="college-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Featured</th>
          </tr>
        </thead>
        <tbody>
          {colleges.map(college => (
            <CollegeRow key={college.id} college={college} />
          ))}
        </tbody>
      </table>
      {isLoading && <p>Loading...</p>}
      {!isLoading && (
        <div className="load-more" onClick={loadMoreColleges}>
          Load More
        </div>
      )}
    </div>
  );
};

export default CollegeTable;
