// CollegeTable.js
import React, { useState } from 'react';
import CollegeRow from './CollegeRow';
import SearchBar from './SearchBar';
import InfiniteScroll from './InfiniteScroll';
import collegesData from '../data/collegesData.json';
import '../styles/CollegeTable.css';

const CollegeTable = () => {
  const [colleges, setColleges] = useState(collegesData.slice(0, 10));

  // Sorting logic
  const [sortBy, setSortBy] = useState(null);
  const [sortOrder, setSortOrder] = useState(null);

  const handleSort = (column) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder('asc');
    }
  };

  const sortedColleges = colleges.sort((a, b) => {
    if (!sortBy) return 0;
    if (sortOrder === 'asc') {
      return a[sortBy] > b[sortBy] ? 1 : -1;
    } else {
      return a[sortBy] < b[sortBy] ? 1 : -1;
    }
  });

  // Search logic
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredColleges = sortedColleges.filter(college =>
    college.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Infinite scroll logic
  const loadMoreData = () => {
    // Add more data or implement your own logic for loading additional data
    console.log('Loading more data...');
  };

  return (
    <div>
      <SearchBar handleSearch={handleSearch} />
      <table className="college-table">
        <thead>
          <tr>
            <th onClick={() => handleSort('name')}>College Name</th>
            <th onClick={() => handleSort('cdRank')}>CD Rank</th>
            <th onClick={() => handleSort('courseFees')}>Course Fees</th>
            <th onClick={() => handleSort('featured')}>Featured</th> {/* Added sorting for Featured column */}
            {/* Add more table headers */}
          </tr>
        </thead>
        <tbody>
          {filteredColleges.map(college => (
            <CollegeRow key={college.id} college={college} />
          ))}
        </tbody>
      </table>
      <InfiniteScroll loadMoreData={loadMoreData} />
    </div>
  );
};

export default CollegeTable;
